const _hoisted_53 = /* @__PURE__ */ createStaticVNode('<h2 id="_2-\u8BED\u6CD5\u793A\u4F8B" tabindex="-1"><a class="header-anchor" href="#_2-\u8BED\u6CD5\u793A\u4F8B" aria-hidden="true">#</a> 2. \u8BED\u6CD5\u793A\u4F8B</h2><p>\u4E0B\u9762\u63D0\u4F9B\u4E86 SQL \u805A\u5408\u8BED\u53E5\u548C\u76F8\u5E94\u7684 MongoDB \u8BED\u53E5\uFF0C\u8868\u4E2D\u7684\u4F8B\u5B50\u5047\u5B9A\u4EE5\u4E0B\u6761\u4EF6\uFF1A</p><ul><li>SQL \u793A\u4F8B\u5047\u5B9A\u6709\u4E24\u4E2A\u8868\uFF1A<code>orders</code> \u548C <code>order_lineitem</code>\uFF0C\u7136\u540E\u901A\u8FC7 <code>order_lineitem.order_id</code> \u548C <code>orders.id</code> \u8FDB\u884C <code>join</code> \u64CD\u4F5C\u3002</li><li>MongoDB \u793A\u4F8B\u5047\u8BBE\u5176\u4E2D\u4E00\u4E2A\u96C6\u5408\uFF08collection\uFF09 <code>orders</code> \u5305\u542B\u4EE5\u4E0B\u539F\u578B\u7684\u6587\u6863\uFF08documents\uFF09\uFF1A</li></ul><div class="language-json ext-json line-numbers-mode"><pre class="language-json"><code><span class="token punctuation">{</span>\n  cust_id<span class="token operator">:</span> <span class="token string">&quot;abc123&quot;</span><span class="token punctuation">,</span>\n  ord_date<span class="token operator">:</span> ISODate(<span class="token string">&quot;2012-11-02T17:04:11.102Z&quot;</span>)<span class="token punctuation">,</span>\n  status<span class="token operator">:</span> &#39;A&#39;<span class="token punctuation">,</span>\n  price<span class="token operator">:</span> <span class="token number">50</span><span class="token punctuation">,</span>\n  items<span class="token operator">:</span> <span class="token punctuation">[</span> <span class="token punctuation">{</span> sku<span class="token operator">:</span> <span class="token string">&quot;xxx&quot;</span><span class="token punctuation">,</span> qty<span class="token operator">:</span> <span class="token number">25</span><span class="token punctuation">,</span> price<span class="token operator">:</span> <span class="token number">1</span> <span class="token punctuation">}</span><span class="token punctuation">,</span>\n           <span class="token punctuation">{</span> sku<span class="token operator">:</span> <span class="token string">&quot;yyy&quot;</span><span class="token punctuation">,</span> qty<span class="token operator">:</span> <span class="token number">25</span><span class="token punctuation">,</span> price<span class="token operator">:</span> <span class="token number">1</span> <span class="token punctuation">}</span> <span class="token punctuation">]</span>\n<span class="token punctuation">}</span>\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="_2-1-count-vs-count" tabindex="-1"><a class="header-anchor" href="#_2-1-count-vs-count" aria-hidden="true">#</a> 2.1 COUNT vs count</h3><p>\u8BA1\u7B97\u6240\u6709 <code>orders</code> \u8BB0\u5F55\u6570\u91CF\uFF1A</p><ul><li>SQL \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>SELECT COUNT(*) AS count\nFROM orders\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><ul><li>MongoDB \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>db.orders.aggregate( [\n   {\n     $group: {\n        _id: null,\n        count: { $sum: 1 }\n     }\n   }\n] )\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="_2-2-sum-vs-sum" tabindex="-1"><a class="header-anchor" href="#_2-2-sum-vs-sum" aria-hidden="true">#</a> 2.2 SUM vs <code>$sum</code></h3><p>\u8BA1\u7B97 <code>orders</code> \u4E2D <code>price</code> \u7684\u603B\u548C\uFF1A</p><ul><li>SQL \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>SELECT SUM(price) AS total\nFROM orders\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><ul><li>MongoDB \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>db.orders.aggregate( [\n   {\n     $group: {\n        _id: null,\n        total: { $sum: &quot;$price&quot; }\n     }\n   }\n] )\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="_2-3-group-by-vs-group" tabindex="-1"><a class="header-anchor" href="#_2-3-group-by-vs-group" aria-hidden="true">#</a> 2.3 GROUP BY vs <code>$group</code></h3><p>\u5BF9\u4E8E\u6BCF\u4E00\u4E2A\u72EC\u7279\u7684 <code>cust_id</code>\uFF0C\u8BA1\u7B97 <code>price</code> \u5B57\u6BB5\u603B\u548C\uFF1A</p><ul><li>SQL \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>SELECT cust_id,\n       SUM(price) AS total\nFROM orders\nGROUP BY cust_id\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><ul><li>MongoDB \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>db.orders.aggregate( [\n   {\n     $group: {\n        _id: &quot;$cust_id&quot;,\n        total: { $sum: &quot;$price&quot; }\n     }\n   }\n] )\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="_2-4-order-by-vs-sort" tabindex="-1"><a class="header-anchor" href="#_2-4-order-by-vs-sort" aria-hidden="true">#</a> 2.4 ORDER BY vs <code>$sort</code></h3><p>\u5BF9\u4E8E\u6BCF\u4E00\u4E2A\u72EC\u7279\u7684 <code>cust_id</code>\uFF0C\u8BA1\u7B97 <code>price</code> \u5B57\u6BB5\u603B\u548C\uFF0C\u4E14\u7ED3\u679C\u6309\u603B\u548C\u6392\u5E8F\uFF1A</p><ul><li>SQL \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>SELECT cust_id,\n       SUM(price) AS total\nFROM orders\nGROUP BY cust_id\nORDER BY total\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><ul><li>MongoDB \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>db.orders.aggregate( [\n   {\n     $group: {\n        _id: &quot;$cust_id&quot;,\n        total: { $sum: &quot;$price&quot; }\n     }\n   },\n   { $sort: { total: 1 } }\n] )\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="_2-5-group-by-multi" tabindex="-1"><a class="header-anchor" href="#_2-5-group-by-multi" aria-hidden="true">#</a> 2.5 GROUP BY Multi</h3><p>\u5BF9\u4E8E\u6BCF\u4E00\u4E2A\u72EC\u7279\u7684 <code>cust_id</code>\uFF0C\u6309\u7167 <code>ord_date</code> \u8FDB\u884C\u5206\u7EC4\uFF0C\u4E14\u4E0D\u5305\u542B\u65E5\u671F\u7684\u65F6\u95F4\u90E8\u5206\uFF0C\u8BA1\u7B97 <code>price</code> \u5B57\u6BB5\u603B\u548C\u3002</p><ul><li>SQL \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>SELECT cust_id,\n       ord_date,\n       SUM(price) AS total\nFROM orders\nGROUP BY cust_id,\n         ord_date\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><ul><li>MongoDB \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>db.orders.aggregate( [\n   {\n     $group: {\n        _id: {\n           cust_id: &quot;$cust_id&quot;,\n           ord_date: { $dateToString: {\n              format: &quot;%Y-%m-%d&quot;,\n              date: &quot;$ord_date&quot;\n           }}\n        },\n        total: { $sum: &quot;$price&quot; }\n     }\n   }\n] )\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="_2-6-having-vs-match" tabindex="-1"><a class="header-anchor" href="#_2-6-having-vs-match" aria-hidden="true">#</a> 2.6 HAVING vs <code>$match</code></h3><p>\u5BF9\u4E8E <code>cust_id</code> \u5982\u679C\u6709\u591A\u4E2A\u8BB0\u5F55\uFF0C\u5C31\u8FD4\u56DE <code>cust_id</code> \u4EE5\u53CA\u76F8\u5E94\u7684\u8BB0\u5F55\u6570\u91CF\uFF1A</p><ul><li>SQL \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>SELECT cust_id,\n       count(*)\nFROM orders\nGROUP BY cust_id\nHAVING count(*) &gt; 1\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><ul><li>MongoDB \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>db.orders.aggregate( [\n   {\n     $group: {\n        _id: &quot;$cust_id&quot;,\n        count: { $sum: 1 }\n     }\n   },\n   { $match: { count: { $gt: 1 } } }\n] )\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="_2-7-where-vs-match" tabindex="-1"><a class="header-anchor" href="#_2-7-where-vs-match" aria-hidden="true">#</a> 2.7 WHERE vs <code>$match</code></h3><p>\u5BF9\u4E8E\u6BCF\u4E00\u4E2A\u72EC\u7279\u7684 <code>cust_id</code>\uFF0C\u4E14 <code>status = \u2018A\u2019</code>\uFF0C\u8BA1\u7B97 <code>price</code> \u5B57\u6BB5\u603B\u548C\uFF0C\u53EA\u6709\u5728\u603B\u548C\u5927\u4E8E 250 \u7684\u60C5\u51B5\u4E0B\uFF0C\u624D\u53EF\u4EE5\u8FD4\u56DE\u3002</p><ul><li>SQL \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>SELECT cust_id,\n       SUM(price) as total\nFROM orders\nWHERE status = &#39;A&#39;\nGROUP BY cust_id\nHAVING total &gt; 250\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><ul><li>MongoDB \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>db.orders.aggregate( [\n   { $match: { status: &#39;A&#39; } },\n   {\n     $group: {\n        _id: &quot;$cust_id&quot;,\n        total: { $sum: &quot;$price&quot; }\n     }\n   },\n   { $match: { total: { $gt: 250 } } }\n] )\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="_2-8-unwind" tabindex="-1"><a class="header-anchor" href="#_2-8-unwind" aria-hidden="true">#</a> 2.8 <code>$unwind</code></h3><p>\u5BF9\u4E8E\u6BCF\u4E00\u4E2A\u72EC\u7279\u7684 <code>cust_id</code>\uFF0C\u5BF9\u76F8\u5E94\u7684\u884C\u7684 item \u9879\u6C42\u548C\u5F97\u5230 <code>qty</code>\uFF1A</p><ul><li>SQL \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>SELECT cust_id,\n       SUM(li.qty) as qty\nFROM orders o,\n     order_lineitem li\nWHERE li.order_id = o.id\nGROUP BY cust_id\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><ul><li>MongoDB \u793A\u4F8B</li></ul><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>db.orders.aggregate( [\n   { $unwind: &quot;$items&quot; },\n   {\n     $group: {\n        _id: &quot;$cust_id&quot;,\n        qty: { $sum: &quot;$items.qty&quot; }\n     }\n   }\n] )\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="_2-9-multi-aggregate" tabindex="-1"><a class="header-anchor" href="#_2-9-multi-aggregate" aria-hidden="true">#</a> 2.9 Multi aggregate</h3><p>\u5C06 <code>cust_id</code>, <code>ord_date</code> \u5206\u7EC4\u5E76\u8BA1\u7B97\u6570\u91CF \uFF0C\u4E0D\u5305\u62EC\u65E5\u671F\u7684\u65F6\u95F4\u90E8\u5206\u3002</p><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>SELECT COUNT(*)\nFROM (SELECT cust_id,\n             ord_date\n      FROM orders\n      GROUP BY cust_id,\n               ord_date)\n      as DerivedTable\ndb.orders.aggregate( [\n   {\n     $group: {\n        _id: {\n           cust_id: &quot;$cust_id&quot;,\n           ord_date: { $dateToString: {\n              format: &quot;%Y-%m-%d&quot;,\n              date: &quot;$ord_date&quot;\n           }}\n        }\n     }\n   },\n   {\n     $group: {\n        _id: null,\n        count: { $sum: 1 }\n     }\n   }\n] )\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h2 id="\u53C2\u8003\u6587\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u6587\u7AE0" aria-hidden="true">#</a> \u53C2\u8003\u6587\u7AE0</h2>', 56);
